import React from "react";
import ReactIconComponent from "../ReactIconComponent/ReactIconComponent";
import styled from "styled-components";

function Header({ iconSize }) {
  return (
    <HeaderWrapper style={{ "--iconSize": iconSize }}>
      <ReactIconComponent size={iconSize} />
    </HeaderWrapper>
  );
}

// the min-height correspon to the Icon size
const HeaderWrapper = styled.header`
  width: 100%;
  min-height: var(--iconSize);
  height: ${36 / 16}rem;
  border: 2px solid blue;
`;

export default Header;
