          <HeaderComponent />
        </header>
        <Home />
      </ArticlesProvider>
    </body>
 */

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

