import React, { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import { COLORS, FONTFAMILY } from "../../Constants";
import { FONTSIZE } from "../../Constants";
import { ArticlesContext } from "../ArticlesProvider";
import { UserContext } from "../UserProvider/UserProvider";
import UserProvider from "../UserProvider/UserProvider";

function ArticleComponent() {
  const { currentArticle } = useContext(ArticlesContext);
  const { currentUser, getCurrentUser } = useContext(UserContext);

  useEffect(() => {
    getCurrentUser(currentArticle.userId);
  }, []);

  return (
    <UserProvider>
      <Wrapper>
        <Article>
          <header>
            <Title>{currentArticle.title}</Title>
          </header>
          <Body>{currentArticle.body}</Body>
        </Article>
        <AuthorWrapper>{currentUser ? currentUser : undefined}</AuthorWrapper>
      </Wrapper>
    </UserProvider>
  );
}

const Wrapper = styled.section`
  padding: 0 ${32 / 16}rem;
`;

const Article = styled.article``;

const Title = styled.h1`
  font-family: ${FONTFAMILY.lustria};
  font-size: ${FONTSIZE.h1};
  text-transform: uppercase;
  text-align: center;
`;

const Body = styled.p`
  font-family: ${FONTFAMILY.lato};
  font-size: ${FONTSIZE.parag};
`;

const AuthorWrapper = styled.section``;

export default ArticleComponent;
